import React, { useState } from 'react';
import './App.css';

const generateRandomString = () => Math.random().toString(36).substr(2, 9);

function App() {
  const [shoppingLists, setShoppingLists] = useState([]);

  const addNewList = () => {
    const newList = {
      id: generateRandomString(),
      name: generateRandomString(),
      items: [],
    };
    setShoppingLists([...shoppingLists, newList]);
  };

  return (
    <div className="App">
      <button onClick={addNewList}>+</button>
      {shoppingLists.map(list => (
        <ShoppingList key={list.id} list={list} setShoppingLists={setShoppingLists} />
      ))}
    </div>
  );
}

function ShoppingList({ list, setShoppingLists }) {
  const [newItem, setNewItem] = useState('');
  const [editItemIndex, setEditItemIndex] = useState(null);
  const [editedValue, setEditedValue] = useState('');

  const addItem = () => {
    if (newItem) {
      list.items.push(newItem);
      setNewItem('');
      updateList(list);
    }
  };

  const editItem = (index) => {
    setEditItemIndex(index);
    setEditedValue(list.items[index]);
  };

  const updateItem = () => {
    if (editedValue) {
      list.items[editItemIndex] = editedValue;
      setEditItemIndex(null);
      setEditedValue('');
      updateList(list);
    }
  };

  const deleteItem = (index) => {
    list.items.splice(index, 1);
    updateList(list);
  };

  const deleteList = (id) => {
    setShoppingLists(prevLists => prevLists.filter(list => list.id !== id));
  };

  const updateList = (updatedList) => {
    setShoppingLists(prevLists => prevLists.map(list => (list.id === updatedList.id ? updatedList : list)));
  };

  return (
    <div className="list">
      <h3>{list.name}</h3>
      <button onClick={() => deleteList(list.id)}>Delete List</button>

      <input 
        value={newItem}
        onChange={(e) => setNewItem(e.target.value)}
        placeholder="New Item..."
      />
      <button onClick={addItem}>Add Item</button>

      <ul>
        {list.items.map((item, index) => (
          <li key={index}>
            {editItemIndex === index ? (
              <>
                <input value={editedValue} onChange={(e) => setEditedValue(e.target.value)} />
                <button onClick={updateItem}>Save</button>
              </>
            ) : (
              <>
                {item} 
                <button onClick={() => editItem(index)}>Edit</button>
                <button onClick={() => deleteItem(index)}>Delete</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
